		}

		for (let i = 0, len = LayerLib.LAYER_EVENTS.length; i < len; i++) {
			const evProp = 'on' + LayerLib.LAYER_EVENTS[ i ].charAt(0).toUpperCase() + LayerLib.LAYER_EVENTS[ i ].substr(1);
			if (vn.props[ evProp ]) {
				map.on(LayerLib.LAYER_EVENTS[ i ], layerId, vn.props[ evProp ]);
			}
		}

	}

